import React, { useState } from "react";
import { styled } from '@mui/material/styles';
import Button from '@mui/material/Button';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import * as XLSX from 'xlsx';

const VisuallyHiddenInput = styled('input')({
    clip: 'rect(0 0 0 0)',
    clipPath: 'inset(50%)',
    height: 1,
    overflow: 'hidden',
    position: 'absolute',
    bottom: 0,
    left: 0,
    whiteSpace: 'nowrap',
    width: 1,
});

const Home = () => {
    const [date, setDate] = useState('');
    const [uniqueDates, setUniqueDates] = useState(JSON.parse(localStorage.getItem('uniqueDates')) || []);

    const handleChange = (event) => {
        setDate(event.target.value);
    };

    const handleFileUpload = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();

        reader.onload = (event) => {
            const binaryString = event.target.result;
            const workbook = XLSX.read(binaryString, {type: 'binary'});
            const sheetName = workbook.SheetNames[0];
            const worksheet = workbook.Sheets[sheetName];
            const data = XLSX.utils.sheet_to_json(worksheet);
            const dates = extractUniqueDates(data);
            setUniqueDates(dates);
            localStorage.setItem('uniqueDates', JSON.stringify(dates));
        };

        reader.readAsBinaryString(file);
    };

    // Function to extract unique dates from data
    const extractUniqueDates = (data) => {
        const dates = new Set();
        data.forEach(item => {
            if (item['Date']) {
                dates.add(item['Date'].toString());
            }
        });
        return Array.from(dates);
    };

    return (
        <>
            <h1>Seating Arrangement</h1>
            <Button component="label" variant="contained" startIcon={<CloudUploadIcon />}>
                Upload Excel Sheet
                <VisuallyHiddenInput
                    type="file"
                    onChange={handleFileUpload}
                />
            </Button>
            <FormControl sx={{ m: 1, minWidth: 300 }} size="small">
                <InputLabel id="demo-select-small-label">Date</InputLabel>
                <Select
                    labelId="demo-select-small-label"
                    id="demo-select-small"
                    value={date}
                    label="Date"
                    onChange={handleChange}
                >
                    {uniqueDates.map((uniqueDate, index) => (
                        <MenuItem key={index} value={uniqueDate}>{uniqueDate}</MenuItem>
                    ))}
                </Select>
            </FormControl>
        </>
    );
};

export default Home;
